import React, {Component} from 'react';
import {StyleSheet, Text, View} from 'react-native';
import ImageColors from 'react-native-image-colors';

export default class App extends Component {
  URL = 'https://i.imgur.com/O3XSdU7.jpg';

  state = {
    status: 'starting',
    colors: null,
  };

  componentDidMount() {
    this.fetchColors();
  }

  fetchColors = async () => {
    const colors = await ImageColors.getColors(this.URL, {
      dominant: true,
      average: true,
      vibrant: true,
      darkVibrant: true,
      lightVibrant: true,
      darkMuted: true,
      lightMuted: true,
      muted: true,
      defaultColor: '#000000',
    });

    if (colors.platform === 'android') {
      // Access android properties
      // e.g.
      const averageColor = colors.average;
    } else {
      // Access iOS properties
      // e.g.
      const backgroundColor = colors.background;
    }

    this.setState({colors, status: 'DONE'});
  };
  render() {
    return (
      <View style={styles.container}>
        <Text style={styles.welcome}>☆ImageColors example☆</Text>
        <Text style={styles.instructions}>STATUS: {this.state.status}</Text>
        <Text style={styles.welcome}>☆NATIVE CALLBACK MESSAGE☆</Text>
        <Text style={styles.instructions}>
          {JSON.stringify(this.state.colors)}
        </Text>
      </View>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#F5FCFF',
  },
  welcome: {
    fontSize: 20,
    textAlign: 'center',
    margin: 10,
  },
  instructions: {
    textAlign: 'center',
    color: '#333333',
    marginBottom: 5,
    marginHorizontal: 10,
  },
});
